x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {
    /**
     * BR runs with roles agent_schedule_user and agent_schedule_manager.  agent_schedule_admin, agent_schedule_manager contain/have role agent_schedule_user
     * 1. allow agent_achedule_admin/itil_admin and schedule_admin to create schedule entries for everyone,
     * 2. allow agents_scheduler_manager to creatte schedule for their reportees
     * 3. agent_schedule_user includes agent_schedule_manager, agent_schedule_manager can create schedule for themself
     */
    var answer = false;
    var isFsmWfoActive = false;
    if (GlidePluginManager.isActive("com.snc.work_management"))
        isFsmWfoActive = new global.FSMWFOUtil().enableFsmWfoLogic();
    if (gs.hasRole("itil_admin") || gs.hasRole("schedule_admin") || gs.hasRole("sn_wfo_cfg_ws.manager") || gs.hasRole("wm_dispatcher") || gs.hasRole("rota_admin") || gs.hasRole("rota_manager") || gs.hasRole("sn_shift_planning.user") || gs.hasRole("sn_wfo.user")) {
        answer = true;
    } else {
        if (isFsmWfoActive) {
            var personalScheduleGR = new sn_fsm_shift_schdl.AgentScheduleWFOUtil().getAgentPersonalSchedule(gs.getUserID(), current.schedule);
            if (personalScheduleGR && personalScheduleGR.next() && (gs.hasRole("sn_shift_planning.user"))) {
                answer = true;
            } else if (gs.hasRole("sn_wfo_cfg_ws.manager") || gs.hasRole("sn_shift_planning.user")) {
                answer = true;
            }
        } else {
            var agentScheduleUtil = new AgentScheduleUtil();
            var user = gs.getUserID();
            var personalScheduleGR = agentScheduleUtil.getAgentPersonalSchedule(user);
            if (personalScheduleGR.next() && (personalScheduleGR.getValue('personal_schedule') == current.schedule) && (gs.hasRole("agent_schedule_user"))) {
                answer = true;
            } else if (gs.hasRole("agent_schedule_manager")) {
                answer = agentScheduleUtil.isReporteeEvent(current);
            }
        }
    }
    if (answer == false) {
        current.setAbortAction(true);
    }
})(current, previous);
