(function executeRule(current, previous /*null when async*/) {
 var week = [];
 week[0] = gs.getMessage("Monday");
 week[1] = gs.getMessage("Tuesday");
 week[2] = gs.getMessage("Wednesday");
 week[3] = gs.getMessage("Thursday");
 week[4] = gs.getMessage("Friday");
 week[5] = gs.getMessage("Saturday");
 week[6] = gs.getMessage("Sunday");

 var weekNames = [];
 // Add your code here
 var startGDT = new GlideDateTime();
 startGDT.setValue(current.start_date + " 00:00:00");


 var endGDT = new GlideDateTime();
 endGDT.setValue(current.end_date + " 23:59:59");


 var duration = GlideDateTime.subtract(startGDT, endGDT);
 var days = parseInt(duration.getRoundedDayPart());

 var bookableDays = current.bookable_days + '';

 if((days < 8) && bookableDays && (bookableDays.length>0)){

  var tempGDT = new GlideDateTime();
  tempGDT.setDisplayValueInternal(current.start_date + " 00:00:00");

  endGDT.setDisplayValueInternal(current.end_date+ " 00:00:00");

  while(tempGDT.getNumericValue() <= endGDT.getNumericValue()){

   var day = tempGDT.getDayOfWeekLocalTime();
   if(bookableDays.indexOf(day) < 0){
    weekNames.push(week[day-1]);
   }
   tempGDT.addDaysLocalTime(1);
  }

  if(weekNames && weekNames.length>0){

   if(current.start_date == current.end_date){
    weekNames = weekNames.toString();
    gs.addErrorMessage(gs.getMessage("The bookable day, {0} is not selected for the configured date, {1}.",[weekNames,current.start_date.getDisplayValue()]));
   }
   else{
    if(weekNames.length == 1){
     weekNames = weekNames.toString();
     gs.addErrorMessage(gs.getMessage("The bookable day, {0} is not selected for the configured dates, starting from {1} to {2}.",[weekNames,current.start_date.getDisplayValue(),current.end_date.getDisplayValue()]));
    }
    else{
     weekNames = weekNames.toString();
     gs.addErrorMessage(gs.getMessage("The bookable days, {0} are not selected for the configured dates, starting from {1} to {2}.",[weekNames,current.start_date.getDisplayValue(),current.end_date.getDisplayValue()]));
    }
   }
   current.setAbortAction(true);
   gs.setRedirect(current);
  }

 }

})(current, previous);
