x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {
 var wsdSyncUtils = new WSDSyncUtils();
 // Creating the link between the 2 campuses
 var indoorCampusGr = new GlideRecord('sn_map_core_campus');
 indoorCampusGr.get(current.getValue('indoor_map_campus'));
 var indoorCampusRecord = wsdSyncUtils.prepareIndoorRecord('campus', indoorCampusGr);
 var wsdObjectSysId = wsdSyncUtils.executeOperation(current, 'campus', 'campus', { operation: 'link', wsd_campus: current.getValue('wsd_campus') }, indoorCampusRecord);

 if (!wsdObjectSysId || wsdObjectSysId === 'skipped') {
  gs.addErrorMessage(gs.getMessage('The campuses cannot be linked, please check the system logs and retry again'));
 } else {
  // Generating sync records for the cleaning step
  var hasGeneratedSyncData = wsdSyncUtils.generateSyncRecordsForCleaning(current);

  if (!hasGeneratedSyncData) {
   current.setValue('state', 'building');
   current.update();

   // Retrieving the previous completed synchronization
   var previousSyncParentGr = new GlideRecord('sn_wsd_indoor_map_sync');
   previousSyncParentGr.addQuery('wsd_campus', current.getValue('wsd_campus'));
   previousSyncParentGr.addQuery('indoor_map_campus', current.getValue('indoor_map_campus'));
   previousSyncParentGr.addQuery('state', 'complete');
   previousSyncParentGr.addQuery('sys_id', '!=', current.getUniqueValue());
   previousSyncParentGr.orderByDesc('sys_updated_on');
   previousSyncParentGr.setLimit(1);
   previousSyncParentGr.query();
   previousSyncParentGr.next();

   // Generating sync records for buildings
   hasGeneratedSyncData = wsdSyncUtils.generateDefaultSyncRecords(previousSyncParentGr, 'building', 'building',
    { campus: current.getValue('indoor_map_campus') },
    { campus: current.getValue('wsd_campus') }
   );

   if (!hasGeneratedSyncData) {
    current.setValue('state', 'complete');
    current.update();
   }
  }
 }
})(current, previous);
