x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {
 var attachment = new GlideSysAttachment();
 var current_image = current.getValue('image_file');
 var previous_image = '';

 if (!gs.nil(previous)) {
  previous_image = previous.getValue('image_file');
 }

 if (previous_image !== current_image) {
  var attachment_gr = new GlideRecord('sys_attachment');
  attachment_gr.addQuery('table_sys_id', current.getUniqueValue());
  if (current_image) attachment_gr.addQuery('sys_id', "!=", current_image);
  attachment_gr.query();
  while (attachment_gr.next()) {
   attachment.deleteAttachment(attachment_gr.getUniqueValue());
  }

  if (current_image) {
   var inputs = {};
   inputs.icon_id = current.getUniqueValue();
   var runner = sn_fd.FlowAPI.getRunner();
   var subflow = runner.subflow('sn_map_core.resize_icon_and_encode_it_in_base64');
   subflow.inBackground().withInputs(inputs).run();
  }
 }
})(current, previous);
