x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {
    var dmnTask = current.getValue('sys_id'); //Demand Task sys id
    var priorASMT;
    var demand = new GlideRecord('dmn_demand_task'); // gliding to demand table
    demand.addQuery('sys_id', dmnTask); //Found the demand Record
    demand.query();
    if (demand.next()) {
        var survey_response = new GlideRecord('asmt_assessment_instance');
        survey_response.addQuery('task_id', demand.parent);
        survey_response.addEncodedQuery('state=complete');
        survey_response.addQuery('metric_type.display_value', 'Demand Assessment');
        survey_response.addQuery('ORDERBYtaken_on');
        survey_response.query();
        if (survey_response.next()) {
            priorASMT = survey_response.sys_id;
        }
    }
    current.correlation_id = priorASMT;
    current.update();
    g_scratchpad.cmsID = current.correlation_id;
})(current, previous);
