(function executeRule(current, previous /*null when async*/) {

 if(current.start_date.dateNumericValue() > current.end_date.dateNumericValue()) {
  gs.addErrorMessage(gs.getMessage('Program start date cannot be later than program end date'));
  current.setAbortAction(true);
  return;
 }

 validateDates();

 function validateDates(){
  var message,abort=false;
  var validator = new ProgramDateValidator(current);
  var validateProjects = validator.programStartsBeforeProjectAndEndsAfterProject();
  var validateDemands = validator.programStartsBeforeDemandAndEndsAfterDemand();
  var validateProgramTasks = validator.programStartsBeforeProgramTaskAndEndsAfterProgramTask();

  if( validateProjects.status == 1){
   abort = true;
   message = gs.getMessage("Cannot change program dates as some projects start before the given program start date");
  } else if( validateProjects.status == 2 ){
   abort = true;
   message = gs.getMessage("Cannot change program dates as some projects end after the given program end date");
  } else if( validateDemands.status == 1 ){
   abort = true;
   message = gs.getMessage("Cannot change program dates as some demands start before the given program start date");
  } else if( validateDemands.status == 2 ){
   abort = true;
   message = gs.getMessage("Cannot change program dates as some demands end after the given program end date");
  }else if( validateProgramTasks.status == 1 ){
   abort = true;
   message = gs.getMessage("Cannot change program dates as some program tasks start before the given program start date");
  } else if( validateProgramTasks.status == 2 ){
   abort = true;
   message = gs.getMessage("Cannot change program dates as some program tasks end after the given program end date");
  }

  if( abort == true ){
   gs.addErrorMessage(message);
   current.setAbortAction(true);
   return;
  }
 }

})(current, previous);
