/**
Service-now.com

Description: set the rollup field on our current and previous parent
 **/
var currentRecordTableName = current.getTableName();
if (currentRecordTableName !== 'dmn_decision' && currentRecordTableName !== 'dmn_requirement')
 checkHasChildren();

   function checkHasChildren() {
      var parent = null;
      var rollup = null;

      //check previous parent
      if (previous && previous.parent != "") {
         parent = previous.parent;
         rollup = hasChildren(parent);
         setRollup(parent,rollup);
      }

      if (current && current.parent != "") {
         parent = current.parent;
         rollup = hasChildren(parent);
         setRollup(parent,rollup);
      }
   }

   function hasChildren(task) {
      gs.log(':: -- hasChildren -- ::' + task.number);
      var gr = new GlideRecord("planned_task");
      gr.addQuery("parent", task.sys_id);
      gr.setLimit(1); //only need one to know
      gr.query();

      if(gr.next()) {
         return true;
      }

      gs.log(':: -- hasChildren -- ::' + false);
      return false;
   }

   function setRollup(task, rollup) {
      gs.log(':: -- setRollup -- ::' + task.number + ' :: ' + rollup);
      var parent = new GlideRecord('planned_task');
      parent.addQuery("sys_id", task.sys_id);
      parent.query();

      if(parent.next()) {
         parent.rollup = rollup ? 1 : 0;
         parent.setWorkflow(false);
         parent.update();
      }
   }
