x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {
 if (!current.start_date.nil() || !current.end_date.nil()){
  var apptConfig = new sn_apptmnt_booking.AppointmentDayConfigUtil();
  var resp = apptConfig.hasOverlapSlots(current).response;

  if (resp.state == 'error') {
   gs.addErrorMessage(resp.message);
   current.setAbortAction(true);
  }
 } else {
  if (!current.service_configuration_rule.nil()) {
   var gr = new GlideRecord("sn_apptmnt_booking_day_configuration");
   gr.addQuery("service_configuration_rule", current.service_configuration_rule);
   gr.addQuery("sys_id", "!=", current.sys_id);
   gr.addActiveQuery();
   gr.setLimit(1);
   gr.query();
   if (gr.next()) {
    var url = gs.getProperty('glide.servlet.uri') + 'sn_apptmnt_booking_day_configuration.do?sys_id=' + gr.sys_id;
    var htmlLink = '<a href="' + url + '">' + gr.name + '</a>';
    var errorMessage = gs.getMessage("There is already an advanced configuration for the rule without a date boundry, {0}", htmlLink);

    gs.addErrorMessage(errorMessage);
    current.setAbortAction(true);
   }
  } else {
   var errorMessage2 = "An advanced configuration must have atleast a service configuration rule or date boundry (start date, end date).";

   gs.addErrorMessage(errorMessage2);
   current.setAbortAction(true);
  }
 }
})(current, previous);
