x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {
    var npe = new SNC.NPEScriptableAPI();

    // If we're already opted in, we shouldn't be modifying Encryption Contexts.
    if (npe.isOptedIn()) {
        gs.addInfoMessage(gs.getMessage("Heads up! Your instance has migrated to Encryption Modules, and is no longer using Encryption Context.\nUpdating this record might have no effect on encryption configuration."));
        return;
    }

    // If no migrations are impending, then we're also good.
    if (!npe.isMigrationImpending())
        return;

    // Display a warning to discourage the user from updating the encrypted field
    // configuration, as it's about to be migrated.
    var url = new GlideURL('sys_mass_encryption_job_list.do');
    url.set('sysparm_query', 'type=all_keys_to_kmf');
    gs.addInfoMessage(gs.getMessage('Warning: a Key Migration is running or scheduled. To reduce the risk of conflicts, Key Migrations should be allowed to complete before updating Encrypted Field Configurations. <a href="{0}">Review Security Jobs</a>', url.toString()));
})(current, previous);
