x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {

    if (current.short_description == 'Review Assessment') {
        //Creation of assessment instance record
        var typeSysID = '32d0083a47743510020ba9f8536d43a7'; //The sys_id of the assessment, should be in published state
        var sourceRecordID = current.parent.sys_id; //sys_id of the transaction record (Ex: sys_id of the IMS record)
        var userID = current.parent.submitter.sys_id; // sys_id in the opened_by field
        var result = new SNC.AssessmentCreation().createAssessments(typeSysID, sourceRecordID, userID); // successfully created the Survey instance record
        //gs.addInfoMessage('result: '+ result );

        //Association of Trigger ID and Trigger Records
        var asmtGR = new GlideRecord("asmt_assessment_instance");
        asmtGR.get(result.split(",")[0]); // gets the sys_id of the newly created assessment record from the 'result' variable array
        asmtGR.setValue('trigger_table', 'dmn_demand'); //setting target table to demand
        asmtGR.setValue('task_id', current.parent.sys_id); //sys_id of the task id to the current record
        asmtGR.update();
    }

})(current, previous);
