function onBefore(current, previous) {
    if ((current.sys_class_name == 'sm_schedule') && !current.propagation_flag) {
        var gr = new GlideRecord("sm_m2m_maint_plan_to_record");
        gr.addQuery("schedule", current.sys_id);
        gr.setValue("active", current.active);
        gr.setValue("propagation_flag", true);
        gr.setValue("propagation_flag", false);
        gr.updateMultiple();
        // In the case we set the schedule active, we need to
        // be sure its plan will be active too
        if (current.active) {
            setSchedulePlanActive(true);
        } else {
            // In the case all the schedules not active, we need to
            // be sure its plan will be not active too
            // Wont be applicable for planned work schedule.
            if (current.sys_class_name == 'sm_schedule') {
                var sch = new GlideRecord("sm_schedule");
                sch.addQuery("plan", current.plan);
                sch.addQuery("active", true);
                sch.addQuery("sys_id", "!=", current.sys_id);
                sch.query();
                if (!sch.hasNext())
                    setSchedulePlanActive(false);
            }
        }
    }
}
function setSchedulePlanActive(value) {
    var plan = new GlideRecord("sm_maint_plan");
    if (plan.get(current.plan)) {
        plan.propagation_flag = true;
        plan.update();
        plan.active = value;
        plan.update();

  plan.propagation_flag = false;
        plan.update();
    }
}
