x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {

 var alias = current.key_alias.toString();
 var version = current.key_version.toString(); 

 //Check if there is a different key with the same alias
 var gr = new GlideRecord('sys_encryption_key');

 gr.addQuery('key_alias', alias);
 gr.addQuery('key_version', current.key_version);
 gr.addQuery('sys_id', '!=', current.sys_id);

 gr.query();
 if(gr.next()) {
  current.setAbortAction(true);
  gs.addErrorMessage(gs.getMessage("A key with the same alias and version already exists: ") + alias + ':' + version);
  return;
 }

 for(var i = 0; i < alias.length; i++) {
  if (/[^\w-]/.test(alias)) {
   current.setAbortAction(true);
   gs.addErrorMessage(gs.getMessage("Invalid key alias. The only valid characters are: letters, digits, '-' and '_'"));
   return;
  }
 }

 current.key_alias = alias.toLowerCase();

})(current, previous);
