x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {

 if(!gs.hasRole('itfm_plan_analyst') && !gs.hasRole('admin') && current.state!=previous.state) {
  gs.addErrorMessage(gs.getMessage('Only Financial Analyst can change the Task state.'));
  current.state = previous.state;
  current.setAbortAction(true);
  return;
 }

 if((current.state != 2) && (previous.state == 1)){
  gs.addErrorMessage(gs.getMessage('You can change budget task to only Published state.'));
  current.state = previous.state;
  current.setAbortAction(true);
 }

 if((current.state != 4) && (previous.state == 2)){
  gs.addErrorMessage(gs.getMessage('You can change budget task to only Pending approval.'));
  current.state = previous.state;
  current.setAbortAction(true);
 }

 if((current.state != 4) && (previous.state == 3)){
  gs.addErrorMessage(gs.getMessage('You can change budget task to only Pending approval.'));
  current.state = previous.state;
  current.setAbortAction(true);
 }

 if(!(current.state == 3 || current.state == 5) && (previous.state == 4)){
  gs.addErrorMessage(gs.getMessage('You can change budget task to only Awaiting Input or Approved state.'));
  current.state = previous.state;
  current.setAbortAction(true);
 }

 if((current.state != 3) && (previous.state == 5)){
  gs.addErrorMessage(gs.getMessage('You can change budget task to only Awaiting Input state.'));
  current.state = previous.state;
  current.setAbortAction(true);
 }

 if((current.state == 2) && (previous.state == 1)){
  var b=new SNC.BudgetingScriptable();
  b.publishTask(current.plan.plan_definition,current.fiscal_period,current.plan);
 }

 if((current.state == 4) && (previous.state == 2 || previous.state == 3)){
  var b=new SNC.BudgetingScriptable();
  b.promotePlan(current.plan,current.fiscal_period);
 }

 if((current.state == 5) && (previous.state == 4)){
  var b=new SNC.BudgetingScriptable();
  b.approveTask(current.plan.plan_definition,current.fiscal_period,current.plan);
 }

})(current, previous);
