// The "When to run" condition assures either the cmdb_ci or asset has changed
(function executeRule(current, previous /*null when async*/) {
 // If the current asset or cmdbi_ci is currently empty and previously non-empty, set the other field to also be empty.
 if (current.asset.nil() && !previous.asset.nil())
  current.cmdb_ci = ""; 
 else if (current.cmdb_ci.nil() && !previous.cmdb_ci.nil())
  current.asset = ""; 

 // If only one field is empty, sync the other field
 else if (!current.asset.nil() && current.cmdb_ci.nil())  
  current.cmdb_ci = current.asset.ci;  
 else if (current.asset.nil() && !current.cmdb_ci.nil())
  current.asset = current.cmdb_ci.asset; 

 // If both fields are filled, but they have changed from the previous value, make sure the other field is kept in sync
 else if (current.asset != previous.asset)
  current.cmdb_ci = current.asset.ci;
 else if (current.cmdb_ci != previous.cmdb_ci)
  current.asset = current.cmdb_ci.asset;
})(current, previous);
