x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {

 var name = current.approval_assign_to;

 if (!name || !current.case_table)
  return;

 var isLegit = false;

 var g = new GlideRecord(current.case_table);
 g.initialize();
 if (g.isValid()) {
  var names = name.split('.');
  // verify that the last field in the dot walk is a reference to a sys user or a sys user group
  for (var i = 0; i < names.length; i++) {
   var ele = g.getElement(names[i]);
   if (ele.getED().getInternalType() != "reference")
    break;

   // see if we are on the last element
   if (i == (names.length - 1)) {
    var tblName = ele.getReferenceTable();
    if (tblName == 'sys_user' || tblName == 'sys_user_group')
     isLegit = true;
    else {
     // it wasn't a sys user or group, see if it extends one of those
     var hier = new GlideTableHierarchy(tblName);
     var rootTable = hier.getRoot();
     if (rootTable == 'sys_user' || rootTable == 'sys_user_group')
      isLegit = true;
    }
   } else {
    g = new GlideRecord(ele.getReferenceTable());
    g.initialize();
    if (!g.isValid())
     break;
   }
  }
 }
 if (!isLegit) {
  gs.addErrorMessage(gs.getMessage('The Approval assign to field must specify a user field or a group field'));
  current.setAbortAction(true);
 }

})(current, previous);
