x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {

    var consumer = current.consumer;
    var household = current.household;
    var isCurrent = new sn_csm_household.HouseHoldUtils().isConsumerCurrentInHousehold(consumer.sys_id, household.sys_id);

    if (!isCurrent) {
        var gr = new GlideRecord("csm_household");
        gr.addQuery("sys_id", household);
        gr.addQuery("head", consumer);
        gr.query();

        if (gr.getRowCount() == 1) {

   var msgArray = [];
   msgArray.push(consumer.getDisplayValue());
   msgArray.push(household.getDisplayValue());
   var msg = gs.getMessage("Consumer {0} was removed as head of {1} household.", msgArray);
            gs.addInfoMessage(msg);

   gr.next();
            gr.setValue("head", "NULL");
   gr.update();
        }
    }
})(current, previous);
