x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous) {

 var rmCostAgg = new RMCostAggregator();
 var recordToUpdate;

 if(current.task.changes() && current.operation() == "update" && JSUtil.notNil(previous.task)){
  var taskRecord = previous.task.getRefRecord();
  var isPreviousTaskDemand = taskRecord.instanceOf('dmn_demand');
  if(!isPreviousTaskDemand || (isPreviousTaskDemand && (taskRecord.getValue('type')!='project' || taskRecord.project != current.task))){
   recordToUpdate = new GlideRecord(taskRecord.getTableName());
   recordToUpdate.get(taskRecord.getValue('sys_id'));
   rmCostAgg.updateTaskAllocatedCost(recordToUpdate);
  }
 }

 if(!current.task.nil()) {
  var currentTaskRecord = current.task.getRefRecord();
  recordToUpdate = new GlideRecord(currentTaskRecord.getTableName());
  recordToUpdate.get(currentTaskRecord.getValue('sys_id'));
  rmCostAgg.updateTaskAllocatedCost(recordToUpdate);
 }

})(current, typeof previous != 'undefined' ?  previous : null);
