function onBefore(current, previous) {
  //This function will be automatically called when this rule is processed.
 if (current.sys_id != current.top_task && current.top_task.calculation_type == "automatic") {
  current.setAbortAction(true);
  gs.addErrorMessage(gs.getMessage('Nested Project under automatic project can not be changed to manual'));
 }

 if (current.calculation_type == "automatic") {
  //Need to set all the phases and tasks as manual, assuming user is notficed 
  //for this BIG change effecting all dates and duration due to recalcualtion

  var grProjectTask = new GlideRecord("pm_project_task");
  grProjectTask.addQuery("top_task", current.sys_id);
  grProjectTask.orderBy("start_date");
  grProjectTask.query();
  while (grProjectTask.next()) {
   grProjectTask.calculation_type = "automatic";
   grProjectTask.setWorkflow(false);
   grProjectTask.update();
  }
 } else {
  if (current.rollup) {
   current.setAbortAction(true);
   gs.addErrorMessage(gs.getMessage('Project having children can not be changed to manual'));
  }
 }
}
