x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {
 var tempArray = current.template.toString();

 var idx =  tempArray.indexOf("assigned_to=");
 var skillIdx = tempArray.indexOf("skills=");
 var assignmentGroupIdx = tempArray.indexOf("assignment_group=");


 if(current.table == 'sn_hr_core_task'){
  if(current.assignment_type ==''){
   current.assignment_type = 'employee';
   if(idx > -1)
    current.assignment_option = 'named_user';
   else if(skillIdx > -1 || assignmentGroupIdx > -1){
    current.assignment_type='fulfiller'; 
    current.assignment_option = 'skills_group';
   }else if(current.assign_to == 'assigned_to')
    current.assignment_type='fulfiller';
  } else if(idx > -1 && current.assignment_type !='fulfiller'){
   current.assignment_option = 'named_user';
   current.assignment_type = 'employee';
  }else if(current.assignment_type !='employee' && idx > -1){
   current.assignment_type = 'fulfiller';
   current.assignment_option = 'named_user';
  }else if(skillIdx > -1 || assignmentGroupIdx > -1){
   current.assignment_option = 'skills_group';
   current.assignment_type='fulfiller';
  } else if(current.assign_to == 'assigned_to'){
   current.assignment_type = 'fulfiller';
   if(idx > -1)
    current.assignment_option = 'named_user';
   else if(skillIdx > -1 || assignmentGroupIdx > -1)
    current.assignment_option = 'skills_group';
  } else if(current.assign_to =='subject_person' || current.assign_to == 'opened_for')
   current.assignment_type ='employee';
  else if(current.assign_to !='' && current.assignment_type !='fulfiller')
   current.assignment_type = 'employee';
 } else {
  if(current.assignment_option ==''){
   if(idx > -1)
    current.assignment_option = 'named_user';
   else if(skillIdx > -1 || assignmentGroupIdx > -1)
    current.assignment_option = 'skills_group';
  }else if(idx > -1){
   current.assignment_option = 'named_user';
  }else if (skillIdx > -1 || assignmentGroupIdx > -1)
   current.assignment_option = 'skills_group';
  else current.assignment_option = '';
  }


})(current, previous);
