x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {
 /*
  If overriding duplicate validation for table extensions, create table specific BRs (with lower order than base table duplicate validation BRs) and set flag current._skip_duplicate_validation=true to skip running duplicate validation BRs of base table.
 */
 if (current.consumer) {
  var isB2B2CActive = GlidePluginManager.isActive('com.sn_csm_b2b_consumers');

  // Allow related party consumer for an account sold product if B2B2C is activated
  if (!current.sold_product.account.nil() && !isB2B2CActive) {
   gs.addErrorMessage(gs.getMessage('The sold product is attached to an account'));
   current.setAbortAction(true);
  }

  /*
     Allow related party consumer with account field for record keeping if :-
     1. Account is populated in sold product, and
     2. B2B2C is activated
  */
  if (current.account && (current.sold_product.account.nil() || !isB2B2CActive)) {
   gs.addErrorMessage(gs.getMessage('Account is not allowed with consumer'));
   current.setAbortAction(true);
  }

  // If household is populated in sold product, only allow household members
  if (gs.tableExists('csm_household_member') && !current.sold_product.household.nil()) {
   var util = new sn_csm_household.HouseHoldUtils();
   if (!util.isConsumerCurrentInHousehold(current.consumer, current.sold_product.household)) {
    gs.addErrorMessage(gs.getMessage('Consumer is not an active member of the household'));
    current.setAbortAction(true);
   }
  }

  var rec = new GlideRecord(global.CSMBaseConstants.SOLD_PRODUCT_RELATED_PARTY);
  rec.addQuery('sold_product', current.sold_product);
  rec.addQuery('consumer', current.consumer);
  rec.setLimit(1);
  rec.query();
  if (rec.next()) {
   /*
    When b2b2c plugin is active we can add a account field for record keeping.
    1. For Edit-Flow -> If b2b2c plugin is activated & we're trying to change the account field -> allow it
    2. If b2b2c plugin is activated & we're trying to change the account field. Allow the change, if there's no duplicate record for the updated consumer.
   */
   if (!(isB2B2CActive && !current.isNewRecord() && (current.account != rec.account)) || previous.consumer != current.consumer) {
    gs.addErrorMessage(gs.getMessage('The sold product is already available for the consumer'));
    current.setAbortAction(true);
   }
   //  If current.consumer not a part of current.account, throw error.
   else if (isB2B2CActive) {
    var ACUtil = new sn_acct_consumer.AccountConsumerUtil();
    if (!gs.nil(current.account) && !ACUtil.isConsumerAssociatedToAccount(current.consumer, current.account)) {
     gs.addErrorMessage(gs.getMessage('The consumer and account are not linked'));
     current.setAbortAction(true);
    }
   }
  }
 }

})(current, previous);
