function onBefore(current, previous) {

 var hrCoreUtil = new hr_CoreUtils();

 if (hrCoreUtil.fieldChangeCheck("assignment_group,assigned_to"))
   hrGroupValidation(current);

 if (hrCoreUtil.fieldChangeCheck("work_notes") && new sn_hr_core.hr_Configuration().getBooleanProperty(sn_hr_core.hr_Constants.ROLLUP_WORK_NOTES, false))
  rollUpWorkNotes(current);

 function rollUpWorkNotes(currentTask) {
  if (currentTask.parent) {
   var parent = new GlideRecord('sn_hr_core_case');
   if (parent.get(currentTask.parent)) {
    parent.work_notes = currentTask.work_notes;
    parent.update();
   }
  }
 }

 function hrGroupValidation(currentTask) {
  if (currentTask.assigned_to != '' && currentTask.assignment_group != '' && !(new hr_CoreUtils()).hasValidAssignee(currentTask)) {
   gs.addErrorMessage(gs.getMessage('The assignee is not a member of the assignment group'));
   currentTask.setAbortAction(true);
  }
 }
}
