x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {
 if (current.parent.changes() || current.operation() == 'delete') {
  var service_offering = new GlideAggregate('service_offering');
  service_offering.addQuery('parent', previous.parent);
  service_offering.addAggregate('COUNT');
  service_offering.query(); 
  if (service_offering.next()) {
   if (service_offering.getAggregate('COUNT') == 1) {
    current.setAbortAction(true);
    if (previous.parent.portfolio_status === 'catalog') 
     gs.addErrorMessage(gs.getMessage('Unable to perform this operation because the parent service {0} is in catalog phase and must have atleast one child offering.', [previous.parent.getDisplayValue()]));
    else {
      gs.addErrorMessage(gs.getMessage('Unable to perform this operation because the parent service {0} is in {1} stage and {2} status and must have atleast one child offering.',
       [previous.parent.getDisplayValue(), previous.parent.life_cycle_stage.getDisplayValue(), previous.parent.life_cycle_stage_status.getDisplayValue()]
      ));
    }
   }
  }
 }
})(current, previous);
