x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {

    if (gs.nil(current.getValue('workplace_location'))){
        gs.addErrorMessage(gs.getMessage("Location can't be empty."));
        current.setAbortAction(true);
        return;
    } else if (current.workplace_location.sys_class_name != WPConstants.TABLES.BUILDING && gs.nil(current.workplace_location.building)) {
        gs.addErrorMessage(gs.getMessage('Please select a building or a location in the building as the workplace location.'));
        current.setAbortAction(true);
        return;
    }

    var buildingTZ = getBuildingTZ();
    if (gs.nil(buildingTZ)) {
        gs.addErrorMessage(gs.getMessage("You must define a building time zone before you can continue."));
        current.setAbortAction(true);
        return;
    }
    var userTZ = gs.getSession().getTimeZoneName();
    if (buildingTZ != userTZ) {
        var msg = [userTZ, buildingTZ];
        gs.addErrorMessage(gs.getMessage("Your session and building time zones must match before you can continue. Please change your session time zone from '{0}' to '{1}'", msg));
        current.setAbortAction(true);
        return;
    }

    current.schedule_time_building_tz = getTimeIn24Hr(current.arrival_schedule_time.getDisplayValue());

    function getBuildingTZ() {
        if (current.workplace_location.sys_class_name == WPConstants.TABLES.BUILDING) {
            return current.workplace_location.time_zone;
        }
        if (current.workplace_location.building) {
            return current.workplace_location.building.time_zone;
        }
    }

    function getTimeIn24Hr(time) {
        if (time.contains('AM') || time.contains('PM')) {
            var period = time.split(' ')[1];
            var timePart = time.split(' ')[0].split(':');
            var hour = timePart[0];
            if (period == 'AM' && hour == '12')
                hour = '00';
            else if (period == 'PM' && hour != '12')
                hour = parseInt(hour) + 12;
            return hour + ':' + timePart[1] + ':' + timePart[2];
        } else
            return time;
    }

})(current, previous);
