x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {

 var aptUtil = new sn_apptmnt_booking.AppointmentBookingUtil();
 var appointmentsExists = aptUtil.hasActiveAppointmentsByCatalog(current.catalog_item, current.future_bookable_max_days);

 if (appointmentsExists) {
  current.setAbortAction(true);

  if(current.active != previous.active)
   gs.addErrorMessage(gs.getMessage("Active appointments found. Before updating the {0}, clear active appointments affected by the change.", "Active flag"));

  if(current.availability_table != previous.availability_table)
   gs.addErrorMessage(gs.getMessage("Active appointments found. Before updating the {0}, clear active appointments affected by the change.", "Availibility table"));

  if(current.location != previous.location)
   gs.addErrorMessage(gs.getMessage("Active appointments found. Before updating the {0}, clear active appointments affected by the change.", "Location"));

  if(current.default_timezone != previous.default_timezone)
   gs.addErrorMessage(gs.getMessage("Active appointments found. Before updating the {0}, clear active appointments affected by the change.", "Timezone"));

  if(current.user_contact != previous.user_contact)
   gs.addErrorMessage(gs.getMessage("Active appointments found. Before updating the {0}, clear active appointments affected by the change.", "User Contact"));

  gs.setRedirect(current);
 }


})(current, previous);
