x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {
 var newCollaborators = current.collaborators;
 var oldCollaborators = previous.collaborators;

 if (!(isCurrentUserAdmin() || isCurrentUserAssignedToPerson()) && invalidUpdate(newCollaborators, oldCollaborators)) {
  gs.addErrorMessage(gs.getMessage('Cannot add/remove other collaborators from list'));
  current.setAbortAction(true);
 } 

 function invalidUpdate(newCollaborators, oldCollaborators) {
  if (!isCurrentUserACollaborator(oldCollaborators))  
   return true;

  if (collaboratorsUpdated(newCollaborators, oldCollaborators)) 
   return true;

  return false;
 }

 function collaboratorsUpdated(newCollaborators, oldCollaborators) {
  var oldCollaboratorsList = [];
  var newCollaboratorsList = [];

  if (oldCollaborators) 
   oldCollaboratorsList = oldCollaborators.split(',');

  if (newCollaborators) 
   newCollaboratorsList = newCollaborators.split(',');

  //new collaborator added - new collaborator in not in old collaborators
  for (var n = 0; n < newCollaboratorsList.length; n++) {
   if (oldCollaboratorsList.indexOf(newCollaboratorsList[n]) < 0) 
    return true;
  }

  //old collaborator is not in new collaborators and is not the current user
  for (var o = 0; o < oldCollaboratorsList.length; o++) {
   if (newCollaboratorsList.indexOf(oldCollaboratorsList[o]) < 0 && oldCollaboratorsList[o] != gs.getUserID()) 
    return true;
  }
  return false;
 }

 function isCurrentUserACollaborator(collaboratorList) {
  if (collaboratorList && (collaboratorList.split(',').indexOf(gs.getUserID()) > -1)) 
   return true; 
  return false;
 }

 function isCurrentUserAssignedToPerson() {
  return new hr_Delegation().isAssignedOrDelegated(current);
 }

 function isCurrentUserAdmin() {
  var hrTables = new GlideTableHierarchy(current.getTableName()).getTables();

  if (hrTables.indexOf('sn_hr_le_case') > -1) {
   if (gs.getUser().hasRole('sn_hr_le.admin')) 
    return true;
  } else if (hrTables.indexOf('sn_hr_core_case') > -1) {
   if (gs.getUser().hasRole('sn_hr_core.admin')) 
    return true;
  }
  return false;
 }
})(current, previous);
