x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {
    var updateActualEffortFromTc = false;
    var curTask = current.task.getRefRecord();

    if (curTask.instanceOf("pm_project") || curTask.instanceOf("pm_project_task")) {
        var topTask = curTask.top_task.getRefRecord();
        if (topTask.instanceOf("pm_project")) {
            if (topTask.update_actual_effort_from_time_card == 'yes')
                updateActualEffortFromTc = true;
        }
        else if (gs.getProperty('com.snc.time_card.update.effort') == 'true')
                updateActualEffortFromTc = true;
    }
    else if (gs.getProperty('com.snc.time_card.update.effort') == 'true')
        updateActualEffortFromTc = true;

    if (!updateActualEffortFromTc)
        return;

    var task = current.task.getRefRecord();
    var currentEffort = task.work_effort.getGlideObject().getNumericValue();
    var additionalEffort = current.total * 60 * 60 * 1000;
    if (current.state == "Recalled")
        additionalEffort *= -1;
    var newEffort = new GlideDuration(currentEffort + additionalEffort);
    task.work_effort = newEffort;
    task.update();

 if (new GlidePluginManager().isActive("com.snc.financial_planning_pmo") && (new GlidePluginManager().isActive("sn_audit") || new GlidePluginManager().isActive("com.sn_audit_advanced")) && PPMAuditTaskHelper.isRelatedToAudit(task)) {
        var taskHelper = new PPMAuditTaskHelper();
        taskHelper.updateActualEffort(task, additionalEffort);
    }

})(current, previous);
