validateDates();

function validateDates() {
    var ptmUtil = new ProjectTaskManagerUtil();
    var res = ptmUtil.validatePlannedDates(current);
    if (res && res.status == 'failure') {
        gs.addErrorMessage(res.message);
        current.setAbortAction(true);
        return;
    }

 var actualWorkGDT = new GlideDateTime();
    actualWorkGDT.setValue(current.work_start);

 var endGDT = new GlideDateTime();
 endGDT.setValue(current.end_date);

 var startGDT = new GlideDateTime();
    startGDT.setValue(current.start_date);


    if (current.operation() == "update" && current.end_date.changes()) {
        if (current.work_start.nil() && startGDT.getNumericValue() > endGDT.getNumericValue()) {
            gs.addErrorMessage(gs.getMessage('Planned end date must be later than the Planned start date.'));
            current.setAbortAction(true);
            return;
        }



        if (!current.work_start.nil() && actualWorkGDT.getNumericValue() > endGDT.getNumericValue()) {
            gs.addErrorMessage(gs.getMessage('Planned end date must be later than the Actual start date.'));
            current.setAbortAction(true);
            return;
        }

        var maxDuration = parseInt(gs.getProperty("com.snc.project.task.max_task_duration", "2600"));
        var taskDuration = new GlideDuration(0);
        if (current.allow_dates_outside_schedule == '1') {
            var diffInMilliSecs = endGDT.getNumericValue() - startGDT.getNumericValue();
            taskDuration.addSeconds(diffInMilliSecs / 1000);
            taskDuration = taskDuration.getDayPart();
        } else {
   var startDate = global.JSUtil.notNil(current.work_start) ? current.work_start : current.start_date;
   var endDate = global.JSUtil.notNil(current.work_end) ? current.work_end : current.end_date;
            var resp = ptmUtil.RecalculateDurationFromEndDate(startDate, endDate, current.sys_id);
            resp = new JSON().decode(resp);
            taskDuration = (resp.status == 'success') ? resp.duration : maxDuration + 1;
        }

        if (taskDuration > maxDuration) {
            gs.addErrorMessage(gs.getMessage('Project task  duration exceeds the maximum duration defined in the property.'));
            current.setAbortAction(true);
        }
    }

}
