x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {
 var additionalParams = {};
 additionalParams[global.CSMRelationshipConstants.IS_CONSUMER_SOLD_PRODUCT] = true;
 var util = new global.CSMRelationshipUtils().getRelationshipEPService(current.getTableName() ||  global.CSMRelationshipConstants.SOLD_PRODUCT_RELATED_PARTY, additionalParams);

 var responsibilitiesWithFullAccess = global.CSMResponsibilityModelUtil.getResponsibilities(current.getTableName() ||  global.CSMRelationshipConstants.SOLD_PRODUCT_RELATED_PARTY, global.CSMRelationshipConstants.ACCESS.FULL, null, true) || [];
 responsibilitiesWithFullAccess.push(global.CSMRelationshipConstants.CONSUMER_SOLD_PRDOUCT_VIEWER);

 var currentResponsibility = current.responsibility+'';

 if(current.operation() == 'insert' && current.consumer){
  if(responsibilitiesWithFullAccess.indexOf(currentResponsibility) == -1)
   return;
  util.syncRelationshipGlideListWithEntity(global.CSMBaseConstants.SOLD_PRODUCT, current.sold_product, global.CSMRelationshipConstants.FIELD_ADDITIONAL_CONSUMERS, current.consumer, '');
 }
 else if(current.operation() == 'delete' && current.consumer){
  if(responsibilitiesWithFullAccess.indexOf(currentResponsibility) == -1)
   return;
  util.syncRelationshipGlideListWithEntity(global.CSMBaseConstants.SOLD_PRODUCT, current.sold_product, global.CSMRelationshipConstants.FIELD_ADDITIONAL_CONSUMERS, '', current.consumer);
 }
 else{
  //update operation
  //any one of sold product or consumer or responsibility could have been updated
  if(!current.sold_product.changes() && !current.responsibility.changes() && !current.consumer.changes())
   return;
  var previousResponsibility = previous.responsibility+'';
  if(responsibilitiesWithFullAccess.indexOf(previousResponsibility) > -1 && previous.consumer)
   util.syncRelationshipGlideListWithEntity(global.CSMBaseConstants.SOLD_PRODUCT, previous.sold_product, global.CSMRelationshipConstants.FIELD_ADDITIONAL_CONSUMERS, '', previous.consumer);
  if(responsibilitiesWithFullAccess.indexOf(currentResponsibility) > -1 && current.consumer)
   util.syncRelationshipGlideListWithEntity(global.CSMBaseConstants.SOLD_PRODUCT, current.sold_product, global.CSMRelationshipConstants.FIELD_ADDITIONAL_CONSUMERS, current.consumer);
 }

})(current, previous);
