(function executeRule(current, previous /*null when async*/) {

 var invalidUpdate = false;

 if ( current.region.changes() && (current.isValidField("site") && current.site.region != current.region) )
  invalidUpdate = true;

 if ( current.isValidField("site") && current.site.changes() && (current.site.region != current.region || (current.isValidField("campus") && current.campus.site != current.site)) )
  invalidUpdate = true;

 if ( current.isValidField("campus") && current.campus.changes() && (current.campus.site != current.site || (current.isValidField("building") && current.building.campus != current.campus)) )
  invalidUpdate = true;

 if ( current.isValidField("building") && current.building.changes() && (current.building.campus != current.campus || (current.isValidField("floor") && current.floor.building != current.building)) )
  invalidUpdate = true;

 if (current.isValidField("floor") && current.floor.changes() && (current.floor.building != current.building || (current.isValidField("area") && !gs.nil(current.area) && current.area.floor != current.floor)) )
  invalidUpdate = true;

 if (current.isValidField("area") && !gs.nil(current.area) && current.area.changes() && current.area.floor != current.floor)
  invalidUpdate = true;

 /* 
 if (current.isValidField("space") && current.space.changes())
  if (gs.nil(current.area))
   if (current.space.floor != current.floor)
    abort = true;
  else if (current.space.area != current.area)
   abort = true;

 if (current.isValidField("room") && current.room.changes())
  if (gs.nil(current.area))
   if (current.room.floor != current.floor)
    abort = true;
  else if (current.room.area != current.area)
   abort = true;
 */

 if (invalidUpdate) {
  var link = '<a href="'+ current.sys_class_name +'.do?sys_id='+ current.sys_id +'">'+ current.number +'</a>';
  gs.addErrorMessage(gs.getMessage("Changes on {0} is aborted due to invalid location hierarchy. Please correct it.", [link]));
  current.setAbortAction(true);
 }

})(current, previous);
