x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {

 var aptUtil = new sn_apptmnt_booking.AppointmentBookingUtil();
 var appointmentsExists = aptUtil.hasActiveAppointmentsByConfig(current.sys_id);

 if (appointmentsExists) {
  current.setAbortAction(true);

  if(current.task_table != previous.task_table)
   gs.addErrorMessage(gs.getMessage("Active appointments found. Before updating the {0}, clear active appointments affected by the change.", "Task table"));

  if(current.availability_method != previous.availability_method)
   gs.addErrorMessage(gs.getMessage("Active appointments found. Before updating the {0}, clear active appointments affected by the change.", "Availibility Method"));

  if(current.active != previous.active)
   gs.addErrorMessage(gs.getMessage("Active appointments found. Before updating the {0}, clear active appointments affected by the change.", "Active flag"));

  gs.setRedirect(current);
 }



})(current, previous);
