(function executeRule(current, previous /*null when async*/) {
 /* Rule contants */
 var functionConstant = "function";
 var conditionConstant = "Condition";
 var actionConstant = "Action";

 /* Valid condition and action names */
  var expectedConditionName = current.name.replace(/\s/g, '').concat(conditionConstant);
 var expectedActionName = current.name.replace(/\s/g, '').concat(actionConstant);

 /* Regular expressions for condition and action names */
 var conditionRegex = new RegExp("^\\s*" + functionConstant + "\\s+" + expectedConditionName + "\\s*\\(.*");
 var actionRegex = new RegExp("^\\s*" + functionConstant + "\\s+" + expectedActionName + "\\s*\\(.*");

 /* If the condition name doesn't match the expected format. */
 if (!conditionRegex.test(current.condition)) {
  gs.addErrorMessage(gs.getMessage("Condition name is not the expected value. The expected value is '{0}'. Please correct Condition Name and try again.", expectedConditionName));
  current.setAbortAction(true);
  return;
 }

 /* If the action name doesn't match the expected format. */
 if (!actionRegex.test(current.action)) {
  gs.addErrorMessage(gs.getMessage("Action name is not the expected value. The expected value is '{0}'. Please correct Action Name and try again.", expectedActionName));
  current.setAbortAction(true);
  return;
 }

})(current, previous);
