x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {
    var location_id;
    var assetId = current.asset;
    var gr = new GlideRecord("alm_asset");
    if (gr.isValid() && gr.get(assetId)) {
        if (GlidePluginManager.isActive('com.sn_eam') || GlidePluginManager.isActive('sn_eam')) {
            var linearGr = FSMLinearAssetUtils.getLinearAsset(assetId);
            if (linearGr) {

                if (linearGr.start_marker && current.start_location.nil()) {
                    current.start_location = linearGr.start_marker.sys_id + "";
                    location_id = linearGr.start_marker.sys_id + "";
                }
                if (linearGr.end_marker && current.end_location.nil()) {
                    current.end_location = linearGr.end_marker.sys_id + "";
                }
                current.linear_work = true;
            }
        } else if (gr.location) {
            location_id = gr.location.sys_id + "";
        }
    }
    current.location = location_id;

})(current, previous);
