(function executeRule(current, previous /*null when async*/ ) {

    var requestedItemUtils = new LFRequestedItemUtils(current);
    var currentStateLRITM = requestedItemUtils.getCurrentState();

    if (currentStateLRITM != requestedItemUtils.states.Open) {
        gs.addErrorMessage(gs.getMessage("Requested item not in Open state cannot be added to or removed from a task."));
        current.setAbortAction(true);
    } else {
        var prevTaskId = previous.getValue("localization_task");

        var prevTaskUtils = new LFTaskUtils(prevTaskId);
        if (prevTaskUtils.hasValidRecord()) {
            gs.addErrorMessage(gs.getMessage("Requested item cannot be removed from a task."));
            current.setAbortAction(true);
        } else {
            validateCurrentTask(current);
        }
    }

    function validateCurrentTask(current) {
        var currentTaskId = current.getValue("localization_task");
        var currentTaskUtils = new LFTaskUtils(currentTaskId);
        if (currentTaskUtils.hasValidRecord()) {
            var currentTaskState = currentTaskUtils.getCurrentState();

            if (currentTaskState != currentTaskUtils.states.Open) {
                gs.addErrorMessage(gs.getMessage("Requested item cannot be added to a task not in Open state."));
                current.setAbortAction(true);
            }
        } else {
            gs.addErrorMessage(gs.getMessage("Requested item cannot be added to invalid task record."));
            current.setAbortAction(true);
        }
    }

})(current, previous);
