x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {
 var deploymentGr = new GlideRecord('sys_now_assist_deployment');
 if (deploymentGr.get('name', current.name)) {
  gs.addErrorMessage(gs.getMessage('An assistant with this name already exists. Please use a different name.'));
  current.setAbortAction(true);
  return;
 }

 // Get context profile associated with deployment
 var contextProfileSysId;
 var deploymentConfigSysId;
 var deploymentConfigGr = new GlideRecord('sys_now_assist_deployment_config');
 if (deploymentConfigGr.get('now_assist_deployment', current.sys_id)) {
  contextProfileSysId = deploymentConfigGr.context_profile;
  deploymentConfigSysId = deploymentConfigGr.getUniqueValue();
 }

 // Update context profile name
 var contextProfileGr = new GlideRecord('sys_cs_context_profile');
 if (contextProfileGr.get(contextProfileSysId)) {
  contextProfileGr.name = current.name;
  contextProfileGr.update();
 }

 // Get search application and search profile associated with deployment
 var searchAppSysId;
 var searchProfSysId;
 var contextProfileSearchGr = new GlideRecord('sys_cs_context_profile_search');
 if (contextProfileSearchGr.get('context_profile', contextProfileSysId)) {
  searchAppSysId = contextProfileSearchGr.search_context_config;
  searchProfSysId = contextProfileSearchGr.search_context_config.search_profile;
 }

 // Update AIS search profile label
 var searchProfileGr = new GlideRecord('ais_search_profile');
 if (searchProfileGr.get(searchProfSysId)) {
  searchProfileGr.label = current.name;
  searchProfileGr.update();
 }

 // Update AIS search application name
 var searchAppGr = new GlideRecord('sys_search_context_config');
 if (searchAppGr.get(searchAppSysId)) {
  searchAppGr.name = current.name;
  searchAppGr.update();
 }

 // Get agent chat config associated with deployment
 var agentChatConfigSysId;
 var configAttributeGr = new GlideRecord('sys_now_assist_deployment_config_attributes');
 configAttributeGr.addQuery('deployment_config', deploymentConfigSysId);
 configAttributeGr.addQuery('name', 'sp_agent_chat_config_sys_id');
 configAttributeGr.query();
 if (configAttributeGr.next()) {
  agentChatConfigSysId = configAttributeGr.value;
 }

 // Update agent chat config name
 var agentChatConfigGr = new GlideRecord('sp_agent_chat_config');
 if (agentChatConfigGr.get(agentChatConfigSysId)) {
  agentChatConfigGr.name = current.name + ' Chat Config';
  agentChatConfigGr.update();
 }


})(current, previous);
