(function executeRule(current, previous /*null when async*/) {

 var queue = '';
 var prevGroups = [];
 if(current.operation() == "delete")
  previous = current;

 if(current.operation() == "update" || current.operation() == "delete") {
  queue = previous.getValue('queue');
  var pGroupsStr = previous.getValue('groups');
  if (!gs.nil(pGroupsStr))
   prevGroups = pGroupsStr.split(",");
 }
 var curGroups = [];
 if (current.operation() == "update" || current.operation() == "insert") {
  queue = current.getValue('queue');
  var cGroupsStr = current.getValue('groups');
  if (!gs.nil(cGroupsStr))
   curGroups = cGroupsStr.split(",");
 }
 var gqpGR = new GlideRecord('awa_group_queue_priority');

 // Look though the groups that were on the record pre-update
 // Ignore any that are still their post-update. Delete any that
 // are not here after the update and are not on any of the queue's
 // other eligibility records
 for (var i=0; i<prevGroups.length; i++) {
  var matchIndex = curGroups.indexOf(prevGroups[i]); 
  if (matchIndex >= 0)
   curGroups.splice(matchIndex, 1);
  else {
   var epGR = new GlideRecord('awa_eligibility_pool');
   epGR.addQuery('groups', 'CONTAINS', prevGroups[i]);
   epGR.addQuery('queue', queue);
   epGR.query();
   if (!epGR.hasNext()) {
    gqpGR.initialize();
    gqpGR.addQuery('group', prevGroups[i]);
    gqpGR.addQuery('queue', queue);
    gqpGR.query();
    if (gqpGR.next())
     gqpGR.deleteRecord();
   }
  }
 }

 // Create a group_queue_priority mapping for each new group association.
 for (var j=0; j<curGroups.length; j++) {
  gqpGR.initialize();
  gqpGR.addQuery('group', curGroups[j]);
  gqpGR.addQuery('queue', queue);
  gqpGR.query();
  if (!gqpGR.hasNext()) {
   gqpGR.setValue('group', curGroups[j]);
   gqpGR.setValue('queue', queue);
   gqpGR.setValue('priority', 100);
   gqpGR.insert();
  }
 }

})(current, previous);
