x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {
 var service_offering = new GlideAggregate('service_offering');
 service_offering.addQuery('parent', current.sys_id);
 service_offering.addAggregate('COUNT');
 service_offering.query();
 if (service_offering.next()) {
  if (service_offering.getAggregate('COUNT') == 0) {
   current.setAbortAction(true);
   var message;
   if (current.life_cycle_stage != previous.life_cycle_stage)
    message = gs.getMessage('A service cannot move to a {0} {1} unless it has least one offering', [current.life_cycle_stage.getDisplayValue().toLowerCase(),current.life_cycle_stage_status.getDisplayValue().toLowerCase()]);
   else
    message = gs.getMessage('A service cannot move to a {0} {1} unless it has least one offering', [new GlideChoiceList().getChoiceList('cmdb_ci_service', 'portfolio_status').getChoice('catalog').getLabel().toLowerCase(),current.portfolio_status.getED().getLabel().toLowerCase()]);

   gs.addErrorMessage(message);
  }
 }
})(current, previous);
