x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {

 /**************** Create default inbox layout *******************************************************************/
 var layoutGr = new GlideRecord('awa_inbox_layout');
 layoutGr.name = "Default "+current.name+" layout";
 layoutGr.short_description = "Default inbox layout for the "+current.name+" Service Channel";
 layoutGr.service_channel = current.sys_id;
 layoutGr.is_default = true;
 layoutGr.order = 1000000;
 layoutGr.card_layout = "<img src=\"awa_card_layout_guide.png\" width=\"349\" height=\"300\"/>";

 // Set initial layout to have only the display field for the workitem table
 var wiGr = new GlideRecord(current.workitem_table);
 var displayName = wiGr.getDisplayName();
 layoutGr.field_1 = displayName;
 layoutGr.insert();

 /*************** Create database view linking work item data to data in the configured workitem_table ************/
 var viewGr = new GlideRecord("sys_db_view");
 viewGr.name = "u_awa_"+current.sys_id+"_work_item";
 viewGr.label = "AWA "+current.name+" Work Item";
 var viewID = viewGr.insert();

 // Include queue table to allow filtering queries by service channel ID
 var vtGr = new GlideRecord("sys_db_view_table");
 vtGr.initialize();
 vtGr.table = 'awa_queue';
 vtGr.order = 100;
 vtGr.variable_prefix = 'q';
 vtGr.where_clause = "q_service_channel = '"+current.sys_id+"'";
 vtGr.view = viewID;
 vtGr.insert();

 // Include awa_work_item table
 vtGr.initialize();
 vtGr.table = 'awa_work_item';
 vtGr.order = 200;
 vtGr.variable_prefix = 'wi';
 vtGr.where_clause = "wi_queue = q_sys_id";
 vtGr.view = viewID;
 vtGr.insert();

 // Include workitem_table configured for this service channel
 vtGr.initialize();
 vtGr.table = current.workitem_table;
 vtGr.order = 300;
 vtGr.variable_prefix = 'doc';
 vtGr.where_clause = "doc_sys_id = wi_document_id";
 vtGr.view = viewID;
 vtGr.insert(); 

})(current, previous);
