x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {

 if (current.marker_latitude && (current.marker_latitude < -90 || current.marker_latitude > 90)) {
  current.setAbortAction(true);
  gs.addErrorMessage(gs.getMessage('Marker latitude field must be between {0} and {1}', [-90, 90]));
 }

 if (current.marker_longitude && (current.marker_longitude < -180 || current.marker_longitude > 180)) {
  current.setAbortAction(true);
  gs.addErrorMessage(gs.getMessage('Marker longitude field must be between {0} and {1}', [-180, 180]));
 }

 var countries = new global.GlideQuery('core_country')
 .where('active', true)
 .where('iso3166_2', current.country)
 .selectOne('iso3166_2');

 if (current.country && countries.isEmpty()) {
  current.setAbortAction(true);
  gs.addErrorMessage(gs.getMessage("The country field requires two characters, ISO 3166-1 Alpha-2 compatible country code"));
 }

 if (current.entering_zoom < 10 || current.entering_zoom > 23) {
  current.setAbortAction(true);
  gs.addErrorMessage(gs.getMessage('Entering zoom field must be between {0} and {1}', [10, 23]));
 }

 try {
  var parsedJSON = JSON.parse(current.getValue('geometry'));
  if (!GeojsonValidation.isGeoJSONObject(parsedJSON)) {
   current.setAbortAction(true);
   IndoorMappingApiHelper.geojsonValidationError();
  }
 } catch(err) {
  current.setAbortAction(true);
  IndoorMappingApiHelper.geojsonValidationError();
 }

})(current, previous);
