x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {

 if (current.resource_type == "group") {
  current.user_resource = "";
  if (current.group_resource.nil() && current.role.nil()) {
   current.setAbortAction(true);
   gs.addErrorMessage(gs.getMessage("Group Resource and role cannot be Empty. Add a valid group/role to the Resource Plan"));
  }
  else if(current.members_preference == "all_members") {
   var noRoleNoSkill = current.role.nil() && current.skills.nil();
   if (noRoleNoSkill) {
    var resourceGroup = new ResourceManagementGroup(current.getValue('group_resource'));
    var groupMembers = resourceGroup.getActiveUserIds();
    if (groupMembers.length == 0) {
     current.setAbortAction(true);
     gs.addErrorMessage(gs.getMessage("Selected group does not have members"));
    }
    current.setValue('members_list', groupMembers.join(','));
   } else
    current.members_list = "";
  }
  else if(current.members_preference == "specific_members") {
   current.skills = "";   
   if (current.members_list.nil()) {
    current.setAbortAction(true);
    gs.addErrorMessage(gs.getMessage("Members List cannot be empty"));
   }
  } else if (current.members_preference == "any_member")
   current.members_list = "";
 }
 else if (current.resource_type == "user") {
  //current.group_resource = "";
  current.members_list = "";
  current.role = "";
  current.skills = "";
  if (current.user_resource.nil()) {
   current.setAbortAction(true);
   gs.addErrorMessage(gs.getMessage("User Resource cannot be Empty. Add a valid user to the Resource Plan"));
  }
 }else{//role resource plan
  if(current.members_preference != "specific_members")
   current.members_list = "";
 }
})(current, previous);
