x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {
    var demand = current.demand.getRefRecord();
    if (current.demand.changes() && !JSUtil.nil(current.demand) && !current.project.changes() && JSUtil.nil(current.project))
        current.project = current.demand.project;
    if (current.project.changes() && !JSUtil.nil(current.project) && !current.demand.changes() && JSUtil.nil(current.demand)) {
        var demandRec = new GlideRecord('dmn_demand');
        demandRec.addQuery('project', current.project);
        demandRec.query();
        if (demandRec.next())
            current.demand = demandRec.sys_id;
    }
    if (current.demand.changes() && JSUtil.notNil(current.demand) && !current.project.changes() && JSUtil.notNil(current.project) && current.getValue('project') !== demand.getValue('project'))
        current.project = '';
    if (current.project.changes() && JSUtil.nil(current.project) && !current.demand.changes() && !JSUtil.nil(current.demand) && demand.getValue('type') !== 'scrum_story')
        current.demand = '';
})(current, previous);
