x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {
 var primaryOfferingUtil = new sn_spm.PrimaryOfferingUtil();

 //check if the primary value changes
 if (current.getDisplayValue('primary') != previous.getDisplayValue('primary')) {
  //If changed to false, make sure one commitment of the type is marked as primary
  if (current.getDisplayValue('primary') == 'false') {
   if (primaryOfferingUtil.isPrimaryCommitmentType(current)) {
    var count = primaryOfferingUtil.getTypePrimaryCount(current.service_commitment.type, current.service_offering.sys_id);
    if (count == 1) {
     gs.addErrorMessage(gs.getMessage('One commitment must be set with primary as true for this offering'));
     current.setAbortAction(true);
    }
   }

  //If changed to true, make sure the commitment is the right type
  } else {
   if (!primaryOfferingUtil.isPrimaryCommitmentType(current)) {
    var availability = new GlideChoiceList().getChoiceList('service_commitment', 'type').getChoice('availability').getLabel();
    var sla = new GlideChoiceList().getChoiceList('service_commitment', 'type').getChoice('SLA').getLabel();
    gs.addErrorMessage(gs.getMessage("The commitment must be of type '{0}' or '{1}' in order to be factored into the performance score calculations", [availability, sla]));
    current.setAbortAction(true);
   }
  }
 }

 //Check if offering or commitment type changed with primary true
 if (current.getDisplayValue('primary') == 'true') {

  if (current.getValue('service_offering')  != previous.getValue('service_offering')) {
   gs.addErrorMessage(gs.getMessage('The service offering cannot be changed if the performance score is already set to true'));
   current.setAbortAction(true);
  }

  if (current.service_commitment.type.getDisplayValue() != previous.service_commitment.type.getDisplayValue()) {
   gs.addErrorMessage(gs.getMessage('The commitment type cannot be changed if the performance score is already set to true'));
   current.setAbortAction(true);
  }
 }
})(current, previous);
