if (current.operation() == "update") {
 handleUpdate();
} else {
 handleInsert(); 
}

function handleUpdate() {
 var errMsgBgnng;

 if (current.name == previous.name && current.element == previous.element && current.language == previous.language) {
  // Allow update to other fields
  gs.log("-----------------sys_documentation: B4BR::NoDuplicates: Keys haven't changed.  Allowing to proceed with the update.");
  return;
 } 

 if (current.name != previous.name) {
  // Prevent name change
  gs.log("-----------------sys_documentation: B4BR::NoDuplicates: Name have changed.  Aborting update.");
  errMsgBgnng = gs.getMessage(GlideMetaData.getTableFieldLabel('sys_documentation','name'));
  current.name.setError(errMsgBgnng  + " " + gs.getMessage("in documentation record can not be changed."));
 } else if (current.element != previous.element) {
  // Prevent element change
  gs.log("-----------------sys_documentation: B4BR::NoDuplicates: Element have changed.  Aborting update.");
  errMsgBgnng = gs.getMessage(GlideMetaData.getTableFieldLabel('sys_documentation','element'));
  current.element.setError(errMsgBgnng  + " " + gs.getMessage("in documentation record can not be changed."));
 } else if (current.language != previous.language) {
  // Prevent language change
  gs.log("-----------------sys_documentation: B4BR::NoDuplicates: Language have changed.  Aborting update.");
  errMsgBgnng = gs.getMessage(GlideMetaData.getTableFieldLabel('sys_documentation','language'));
  current.language.setError(errMsgBgnng  + " " + gs.getMessage("in documentation record can not be changed."));
 }
 current.setAbortAction(true);
}

function handleInsert() {
 // Prevent duplicate table entries
 gs.log("-----------------sys_documentation: B4BR::NoDuplicates: Keys changed or insert. Doing validation.");
 var label = new GlideRecord('sys_documentation');
 label.addQuery('name',current.name);
 label.addQuery('element',current.element);
 label.addQuery('language',current.language);

 label.query();
 if (label.next()) {
  var errMsg = gs.getMessage("A documentation record with the same values of") + " " + 
      gs.getMessage(GlideMetaData.getTableFieldLabel('sys_documentation','name')) + gs.getMessage(",") + " " +
   gs.getMessage(GlideMetaData.getTableFieldLabel('sys_documentation','element')) + " " + gs.getMessage("and") + " " +
   gs.getMessage(GlideMetaData.getTableFieldLabel('sys_documentation','language')) + " " + 
   gs.getMessage("fields already exists");
  gs.addErrorMessage(errMsg);
  current.setAbortAction(true);
 }
}
