x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous) {
 var class_name = current.sys_class_name + '';
 if (class_name === 'sn_eam_linear_asset' || class_name === 'sn_eam_linear_segment') {
  return;
 }
 var modelGr = current.model;
 if (gs.nil(modelGr)) {
  gs.addErrorMessage(gs.getMessage('Model cannot be empty for enterprise-class assets'));
  current.setAbortAction(true);
  return;
 }
 if (gs.nil(current.model_category)) {
  // If model category is not populated, default it to the model's category
  current.model_category = modelGr.cmdb_model_category;
 }

 var modelCategoryGr = current.model_category;
 if (!EnterpriseContentEAMUtils.isEnterpriseModelCategory(modelCategoryGr + '')) {
  gs.addErrorMessage(gs.getMessage('Enterprise-class assets cannot have non-enterprise model categories'));
  current.setAbortAction(true);
  return;
 }
 var modelClass = modelCategoryGr.product_model_class + '';
 var assetClass = modelCategoryGr.asset_class + '';
 if (current.sys_class_name + '' !== assetClass || modelGr.sys_class_name + '' !== modelClass) {
  if (!(modelGr.sys_class_name + '' === 'cmdb_model' && modelGr.name + '' === 'Unknown')) {
   gs.addErrorMessage(gs.getMessage('Asset class, model class and model category don\'t match, enterprise-class assets cannot be created or updated'));
   current.setAbortAction(true);
   return;
  }
  // PRB1631560: An unknown model in the base model class is allowed
 }
}(current, previous));
