x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {

 var ga = new GlideAggregate('user_has_schedule');
 ga.addAggregate('COUNT');
 ga.addQuery('user', current.getValue('user'));
 ga.addQuery('context', current.getValue('context'));
 if(current.operation() == 'update') {
  ga.addQuery('sys_id', '!=', current.getValue('sys_id'));
 }
 ga.setGroup(false);
 ga.query();

 var rowAlreadyExists = false;
 if (ga.next()) {
  var count = ga.getAggregate('COUNT');
  if (JSUtil.notNil(count))
   rowAlreadyExists = parseInt(count) > 0;
 }

 if (rowAlreadyExists) {
  current.setAbortAction(true);
  gs.addErrorMessage(gs.getMessage('User is already associated with a schedule'));
 }

})(current, previous);
