x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/) {

 var usePlannedTaskRollupApi = true;
 if (current.instanceOf('pm_project_task') || current.instanceOf('pm_project')) {

  var topTask = current.top_task.getRefRecord();

  // These fields are part of project_management_v3
  if (topTask.isValidField('time_card_preference') && topTask.getValue('time_card_preference') != 'no_time_card' && topTask.isValidField('update_actual_effort_from_time_card') && topTask.getValue('update_actual_effort_from_time_card') == 'yes') {

   usePlannedTaskRollupApi = false;
   var actualEffortApi = new TimeCardActualEffortRollup();
   actualEffortApi.updateTaskActualEffort(current.parent.getRefRecord(), current.getValue('sys_id'));
  }

  if (JSUtil.notNil(current.getValue('parent')) && current.instanceOf('pm_project_task')) {
   var rollupStoriesEffort = new StoryEffortRollup();
   rollupStoriesEffort.updateEffortToProject(current, current.work_effort.getName(), current.parent);
  }
 }

 if (GlidePluginManager.isActive('com.snc.time_card') && current.instanceOf('rm_scrum_task') && JSUtil.notNil(current.getValue('story'))) {
        var actualEffortApi = new TimeCardActualEffortBaseRollUp();
  actualEffortApi.updateTaskActualEffort(current.story.getRefRecord(), current.getValue('sys_id'));
  usePlannedTaskRollupApi = false;
    }

 if (usePlannedTaskRollupApi) {
  var ptRollupApi = new PlannedTaskRollupApi();
  ptRollupApi.rollupActualEffort(current, previous);
 }

})(current, previous);
