function onBefore(current, previous) {
 if (current.job_type == 'recover_db' || current.job_type == 'lock_database' ||
  current.job_type == 'service_catalog_variable_encryption' || current.job_type == 'service_catalog_variable_decryption' ||
  current.job_type == 'mass_key_rotation' || current.job_type == 'single_key_rotation' ||
  (current.job_type == 'mass_recover_order' && current.all_fields + '' == 'true')) {
  current.table = '';
  current.column = '';
  return;
 } else if (current.job_type != 'mass_recover_order')
  current.all_fields = '';


 var jobType = current.getValue('job_type');
 var record = new GlideRecord(current.table);
 var isInvalidTable = !record.isValid();

 var isNotHandlingAttachments = !(jobType == 'mass_encrypt_attachment' || jobType == 'mass_decrypt_attachment' || jobType == 'attachment_key_rotation');

 if (isInvalidTable || (isNotHandlingAttachments && !record.isValidField(current.column))) {
  gs.addErrorMessage(gs.getMessage('The selected {0} does not exist. Cannot schedule job.', (isInvalidTable) ? 'table' : 'field'));
  current.setAbortAction(true);
  return;
 }

 var errorMsg;
 var activeConfig = false;
 var elementDescriptor;

 if (current.job_type != 'mass_encrypt_attachment' &&
  current.job_type != 'mass_decrypt_attachment' &&
  current.job_type != 'attachment_key_rotation') {
  var element = new GlideRecord(current.table).getElement(current.column);
  if (element === undefined || (element + '' == 'null')) {
   gs.addErrorMessage(gs.getMessage('GlideElement undefined for column {0} for table {1}', [current.column, current.table]));
   current.setAbortAction(true);
   return;
  }
  elementDescriptor = element.getED();
  activeConfig = elementDescriptor.isEdgeEncrypted();
 } else {
  current.column = '';
  elementDescriptor = new GlideRecord(current.table).getED();
  activeConfig = elementDescriptor.hasAttachmentsEncrypted();
 }

 if (jobType == 'mass_encrypt') {
  if (!activeConfig) {
   errorMsg = gs.getMessage('Encryption Configuration for table: {0} column: {1} must be set to active before scheduling a mass column encryption.', [current.table, current.column]);
   gs.addErrorMessage(errorMsg);
   current.setAbortAction(true);
   return;
  }

 } else if (jobType == 'mass_encrypt_attachment') {
  if (!activeConfig) {
   errorMsg = gs.getMessage('Encryption Configuration for attachments on table: {0}  must be set to active before scheduling a mass attachment encryption.', current.table);
   gs.addErrorMessage(errorMsg);
   current.setAbortAction(true);
   return;
  }
 } else if (jobType == 'mass_decrypt') {
  if (activeConfig) {
   errorMsg = gs.getMessage('Encryption Configuration for table: {0} column: {1} must be set to inactive before scheduling a mass column decryption.', [current.table, current.column]);
   gs.addErrorMessage(errorMsg);
   current.setAbortAction(true);
   return;
  }
 } else if (jobType == 'mass_decrypt_attachment') {
  if (activeConfig) {
   errorMsg = gs.getMessage('Encryption Configuration for attachments on table: {0}  must be set to inactive before scheduling a mass attachment decryption.', current.table);
   gs.addErrorMessage(errorMsg);
   current.setAbortAction(true);
   return;
  }
 }

 if (current.job_type == 'mass_recover_order' && !elementDescriptor.getEncryptionType().endsWith('order')) {
  gs.addErrorMessage(gs.getMessage('{0}.{1} encryption is not preserving order. Job type invalid', [current.table, current.column]));
  current.setAbortAction(true);
 }

}
