x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {

    var states = {
        'ready': 10,
        'wip': 18,
        'complete': 3,
        'canceled': 7
    };
    var gr = new GlideRecord('sn_hr_core_task');
    gr.addQuery('survey_instance', current.sys_id);
    gr.addActiveQuery();
    gr.setLimit(1);
    gr.query();
    if (gr.next()) {
        if (states.hasOwnProperty(current.state))
            gr.setValue('state', states[current.state]);
        else
            gr.setValue('state', 10);

        gr.update();
        if (gr.state == '3') {
            if (gr.getValue('hr_task_type') == 'collect_Information')
                new sn_hr_sp.EmployeeDataCollection().saveUpdatedData(gr.sys_id);
            gs.addInfoMessage(gs.getMessage('The to-do "{0}" has been completed ', gr.getValue('short_description')));
        }

    }
})(current, previous);
