x_938076_now_utils_now_utils_business_ru.script:
buildScratchPad();

function buildScratchPad() {
 g_scratchpad._lockAsset = false;
 var sc = new sn_sm.SMConfiguration();
 var sf = new SMFilters();
 var draft = SMConstants.ORDER_DRAFT;
 var a_qual = SMConstants.ORDER_AWAITING_QUALIFICATION;
 var isFSMActive = GlidePluginManager.isActive("com.snc.fsm_territory_planning");
 var wf;
 if (isFSMActive)
  wf = new WMFilters();
 g_scratchpad.vendor_group_type = sc.getGroupType(current, "vendor");
 g_scratchpad.qualification_property = sc.isEnabled(current, "qualification", false);
 g_scratchpad.managed_docs = sc.isEnabled(current, "managed_docs", false);
 g_scratchpad.time_card = sc.isEnabled(current, "time_card", false);
 g_scratchpad.parts = sc.isEnabled(current, "part_requirement", false);
 g_scratchpad.request_driven_mode = sc.isEnabled(current, 'request_driven', true);
 g_scratchpad.show_tasks = showTaskTable(sc) ? '' : sc.getTaskTable(current);
 g_scratchpad.enableSF = sc.isEnabled(current, "use_sf", true);
 g_scratchpad.current_short_description = current.getValue('short_description');
 g_scratchpad.task_activities = sc.isEnabled(current, "task_activities", false);

 if (sc.isEnabled(current, "qualification", false)) {
  if (current.state != draft) {
   g_scratchpad.qualification_filter = isFSMActive ? wf.getMatchingQualificationGroups(current) : sf.filterGroups(current.sys_class_name, current.location, 'qualification');
   if (!current.location.nil() && current.state == a_qual && current.qualification_group.nil()) {
    if ('sys_idIN' != g_scratchpad.qualification_filter) {
     var msg = gs.getMessage('Qualification groups cover this location. Select the group to handle this order.');
     gs.addInfoMessage(msg);
    }
   }
  }
 }

 g_scratchpad.associated_task_tables = JSON.stringify(getAssociatedTables(sc));
}

function getAssociatedTables(sc) {
    var config = sc.getConfigRecord(current);
    var tables = (config.task_table + ',' + config.associated_task_tables).split(',');
    var tableSet = {};
    var tablesAndLabels  = [];
    for(var i = 0; i < tables.length; i++) {
        if (tables[i] == '' || tables[i] in tableSet)
            continue;
        tableSet[tables[i]] = true;
        var gr = new GlideRecord(tables[i]);
        if (gr.isValid())
            tablesAndLabels.push({name: tables[i], label: gr.getLabel()});
    }
    return tablesAndLabels;
}

function showTaskTable(sc) {
 var tableExceptions = {
  'hr_case': true,
  'sn_si_incident': true
 };
 var isTaskDriven = !sc.isEnabled(current, 'request_driven', false);
 var requestHasTasks = (new SMTask()).hasSubTask(current, true);
 var currentTableIsException = (current.getTableName() + '') in tableExceptions;
 return (isTaskDriven || requestHasTasks || currentTableIsException);
}
