(function executeRule(current, previous) {

 var demandCurrency = current.cost_plan.demand_currency;
 var functionalCurrency  = current.cost_plan.functional_currency;
 if (!demandCurrency) return;
 if (demandCurrency) {
  if (demandCurrency == functionalCurrency) {
  current.setValue(PmFieldConstants.COST_DEMAND_CURRENCY, current.cost_default_currency);
  current.setValue(PmFieldConstants.COST_EXCHANGE_RATE_DEMAND_CURRENCY, current.exchange_rate);
  current.setValue(PmFieldConstants.COST_REFERENCE_RATE_DEMAND_CURRENCY, current.fx_rate);
  current.setValue(PmFieldConstants.DEMAND_EXCHANGE_RATE_DATE, current.exchange_rate_date);
 } else if (demandCurrency == current.entered_currency) {
  current.setValue(PmFieldConstants.COST_DEMAND_CURRENCY, current.cost_local_currency);
  current.setValue(PmFieldConstants.COST_EXCHANGE_RATE_DEMAND_CURRENCY, "");
  current.setValue(PmFieldConstants.COST_REFERENCE_RATE_DEMAND_CURRENCY, "");
  current.setValue(PmFieldConstants.DEMAND_EXCHANGE_RATE_DATE, ""); 
 }else {
  var fpService = new FinancialsForPPM();
        var exchangeRates = fpService.getExchangeRates([current.fiscal_period], current.entered_currency, current.getValue(PmFieldConstants.DEMAND_CURRENCY));
        if (exchangeRates.exchangeRatesSysIds.length) {
            current.setValue(PmFieldConstants.COST_REFERENCE_RATE_DEMAND_CURRENCY, exchangeRates.exchangeRatesSysIds[0]);
            current.setValue(PmFieldConstants.COST_EXCHANGE_RATE_DEMAND_CURRENCY , exchangeRates.exchangeRates[0]);
   current.exchange_rate_demand_currency_date = exchangeRates.exchangeRateDates[0];
            var currencyService = new SNC.FMCurrency();
            if (exchangeRates.exchangeRatesSysIds[0]){
                current.setValue(PmFieldConstants.COST_DEMAND_CURRENCY, currencyService.calculateAmount(current.cost_local_currency, exchangeRates.exchangeRatesSysIds[0]));
   }
            else {
                current.setValue(PmFieldConstants.COST_DEMAND_CURRENCY, current.cost_local_currency);
            }

        } else {
   current.setValue(PmFieldConstants.COST_DEMAND_CURRENCY, current.cost_local_currency);
   current.setValue(PmFieldConstants.COST_REFERENCE_RATE_DEMAND_CURRENCY, "");
            current.setValue(PmFieldConstants.COST_EXCHANGE_RATE_DEMAND_CURRENCY , "");
  }
  if ((current.entered_currency == functionalCurrency) && (functionalCurrency == current.demand_currency))
  current.exchange_rate_demand_currency_date = "";

 }
 }

})(current, previous);
