x_938076_now_utils_now_utils_business_ru.script:
(function executeRule(current, previous /*null when async*/ ) {
    var arrayUtil = new ArrayUtil();
    var lfConstants = new LFConstants();
    var allArtifactDetails = new LFArtifactUtils().getAllArtifactConfigurations();
    var allArtifacts = allArtifactDetails.allArtifacts;
    var allArtifactInternalNames = allArtifactDetails.sortedArtifactInternalNames;
    var allLanguageDetails = new LFUtils().getAllLanguages();
    var allLanguages = allLanguageDetails.allLanguages;
    var allLanguageSysIds = allLanguageDetails.sortedLanguageSysIds;

    g_scratchpad.allArtifacts = allArtifacts;
    g_scratchpad.allArtifactInternalNames = allArtifactInternalNames;
    g_scratchpad.currentArtifact = current.artifact || '';
    g_scratchpad.allLanguages = allLanguages;
    g_scratchpad.allLanguageSysIds = allLanguageSysIds;
    g_scratchpad.artifactsWithNoProcessorScripts = new LFArtifactUtils().getAllArtifactsWithNoProcessorScripts();
    var tmsV1Configurations = LFTMSConfigurationUtils.getTMSV1Configurations();
    g_scratchpad.tmsV1Configurations = tmsV1Configurations;

    var configuredLanguagesForArtifact = {};
    var gr = new GlideRecord('sn_lf_setting');
    gr.addQuery('sys_id', '!=', current.getUniqueValue());
    gr.query();
    while (gr.next()) {
        var artifact = gr.getValue('artifact');
        var languages = gr.getValue('languages').split(",");
        if (artifact === 'all_artifacts') {
            var artifacts = Object.keys(allArtifacts);
            for (var artifactIdx = 0; artifactIdx < artifacts.length; artifactIdx++) {
                addToConfiguredLanguages(configuredLanguagesForArtifact, artifacts[artifactIdx], languages);
            }
        } else {
            addToConfiguredLanguages(configuredLanguagesForArtifact, artifact, languages);
        }
    }

    g_scratchpad.configuredLanguagesForArtifact = configuredLanguagesForArtifact;

    g_scratchpad.isDtEnabled = GlidePluginManager.isActive(lfConstants.DYNAMIC_TRANSLATION_PLUGIN_ID) ? 'true' : 'false';

    if (GlidePluginManager.isActive(lfConstants.LOCALIZATION_FRAMEWORK_SPOKE_PLUGIN_ID)) {
        var tmsConfigurationUtils = new LFTMSConfigurationUtils(lfConstants.LOCALIZATION_FRAMEWORK_HUB_TMS_NAME);
        g_scratchpad.lfHubTMSReference = tmsConfigurationUtils.getSysId();
    }

    gs.addInfoMessage(gs.getMessage('Configure translation and workflow preferences for a set of artifact - language pair(s). {0}Learn more{1}', ['<a href="/api/now/v1/context_doc_url/Localization_Framework_Settings" target="_blank">', '</a>']));

    function addToConfiguredLanguages(configuredLanguagesForArtifact, artifact, languages) {
        if (!configuredLanguagesForArtifact.hasOwnProperty(artifact)) {
            configuredLanguagesForArtifact[artifact] = [];
        }
        configuredLanguagesForArtifact[artifact] = arrayUtil.union(configuredLanguagesForArtifact[artifact], languages);
    }

})(current, previous);
